    private void btnEscribirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEscribirActionPerformed
        // TODO add your handling code here:
        String codPedido = txtCodPedido.getText();
        String fechaHoraPedido = txtFechaHoraPedido.getText();
        String estadoPedido = txtEstadoPedido.getText();
        // Declarar objeto Cliente
        Pedido pedido = new Pedido();
        pedido.setCodPedido(codPedido);
        pedido.setFechaHoraPedido(fechaHoraPedido);
        pedido.setEstadoPedido(estadoPedido);
        // Enviar mensaje de confirmacion
        lblMensaje.setText("Se escribio los atributos del objeto cliente");
    }//GEN-LAST:event_btnEscribirActionPerformed

